mapper.select:
SELECT user_event_log.id AS id, user_event_log.user_id AS userId, user.username AS username, user_event_log.event_type AS eventType, user_event_log.target_type AS targetType, user_event_log.target_id AS targetId, user_event_log.created_at AS createdAt, article.title AS articleTitle, product.name AS productName, brand.name AS brandName FROM user_event_log LEFT JOIN user ON user.id = user_event_log.user_id LEFT JOIN article article ON user_event_log.target_type = 'article' AND user_event_log.target_id = article.id LEFT JOIN product product ON user_event_log.target_type = 'product' AND user_event_log.target_id = product.id LEFT JOIN brand brand ON user_event_log.target_type = 'brand' AND user_event_log.target_id = brand.id
AND user_event_log.event_type = #{userEventLog.eventType}
AND user_event_log.target_type = #{userEventLog.targetType}
AND user_event_log.created_at >= #{userEventLog.startTime}
AND user_event_log.created_at <= #{userEventLog.endTime}
ORDER BY user_event_log.id DESC
